import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import './CourierAssignment.css';

const CourierAssignment = ({
  isOpen,
  onClose,
  selectedOrders,
  onAssignCourier,
  couriers,
  orders,
  selectedCourier,
  setSelectedCourier
}) => {
  const { t } = useTranslation();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (isOpen) {
      setSearchTerm('');
    }
  }, [isOpen]);

  if (!isOpen) return null;

  const availableCouriers = couriers.filter(courier => 
    !orders.some(order => order.status === 'On Their Way' && order.courier === courier)
  );

  const filteredCouriers = availableCouriers.filter(courier => 
    courier.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleAssign = () => {
    onAssignCourier(selectedCourier);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{t('assign_courier')}</h2>
        <input
          type="text"
          className="courier-search"
          placeholder={t('search_couriers')}
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select 
          value={selectedCourier} 
          onChange={(e) => setSelectedCourier(e.target.value)}
          className="courier-dropdown"
        >
          <option value="">{t('select_courier')}</option>
          {filteredCouriers.map(courier => (
            <option key={courier} value={courier}>{courier}</option>
          ))}
        </select>
        <div className="modal-actions">
          <button 
            onClick={handleAssign} 
            className="assign-button" 
            disabled={!selectedCourier || selectedOrders.length === 0}
          >
            {t('assign')} ({selectedOrders.length})
          </button>
          <button onClick={onClose} className="cancel-button">
            {t('cancel')}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourierAssignment;
