import React, { useState, useCallback, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useParams } from 'react-router-dom';
import UserList from './UserList';
import UserForm from './UserForm';
import './Users.css';

const Users = () => {
  const { t, i18n } = useTranslation();
  const { businessId } = useParams();
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const isRTL = i18n.language === 'he';

  useEffect(() => {
    const fetchUsers = async () => {
      setIsLoading(true);
      try {
        const response = await fetch(`/api/users/business/${businessId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        const data = await response.json();
        setUsers(data);
        setError(null);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchUsers();
  }, [businessId]);

  const addUser = (user) => {
    // Here you would typically make an API call to add the user
    // For now, we'll just update the local state
    setUsers([...users, { ...user, id: Date.now() }]);
    setIsFormVisible(false);
  };

  const updateUser = (updatedUser) => {
    // Here you would typically make an API call to update the user
    // For now, we'll just update the local state
    setUsers(users.map(user => user.id === updatedUser.id ? updatedUser : user));
    setEditingUser(null);
    setIsFormVisible(false);
  };

  const deleteUser = (userId) => {
    // Here you would typically make an API call to delete the user
    // For now, we'll just update the local state
    setUsers(users.filter(user => user.id !== userId));
  };

  const toggleAvailability = (userId) => {
    // Here you would typically make an API call to toggle availability
    // For now, we'll just update the local state
    setUsers(users.map(user => {
      if (user.id === userId) {
        const newAvailability = user.availability === 'in_shift' ? 'out_of_shift' : 'in_shift';
        return { ...user, availability: newAvailability };
      }
      return user;
    }));
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setIsFormVisible(true);
  };

  const handleNewUser = () => {
    setEditingUser(null);
    setIsFormVisible(true);
  };

  const handleCloseForm = () => {
    setIsFormVisible(false);
    setEditingUser(null);
  };

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value.toLowerCase());
  }, []);

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm) ||
    (user.email && user.email.toLowerCase().includes(searchTerm))
  );

  if (isLoading) return <div>{t('loading')}</div>;
  if (error) return <div>{t('error')}: {error}</div>;

  return (
    <div className={`users-container ${isRTL ? 'rtl' : 'ltr'}`}>
      <h1>{t('users_management')}</h1>
      <div className="users-actions">
        <div className="search-container">
          <input
            type="text"
            placeholder={t('search_users')}
            value={searchTerm}
            onChange={handleSearchChange}
            className="search-input"
          />
        </div>
        <button onClick={handleNewUser} className="new-user-button">{t('new_user')}</button>
      </div>
      <UserList 
        users={filteredUsers} 
        onEdit={handleEdit} 
        onDelete={deleteUser}
        onToggleAvailability={toggleAvailability}
      />
      {isFormVisible && (
        <div className="side-panel visible">
          <button onClick={handleCloseForm} className="close-panel">Ã—</button>
          <UserForm 
            onSubmit={editingUser ? updateUser : addUser} 
            initialData={editingUser} 
            onClose={handleCloseForm}
          />
        </div>
      )}
    </div>
  );
};

export default Users;
