import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Header.css';

const Header = () => {
  const { t, i18n } = useTranslation();
  const isRTL = i18n.language === 'he';
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className={`app-header ${isRTL ? 'rtl' : ''}`}>
      <div className="header-content">
        <h1 className="app-title">
          <Link to="/" className="home-link">{t('app_title')}</Link>
        </h1>
        <nav className={`main-nav ${isMenuOpen ? 'active' : ''}`}>
          <ul>
            <li><Link to="/orders" onClick={closeMenu}>{t('nav_orders')}</Link></li>
            <li><Link to="/users" onClick={closeMenu}>{t('nav_users')}</Link></li>
            <li><Link to="/analytics" onClick={closeMenu}>{t('nav_analytics')}</Link></li>
            <li><Link to="/settings" onClick={closeMenu}>{t('nav_settings')}</Link></li>
          </ul>
        </nav>
        {location.pathname !== '/' && (
          <Link to="/" className="menu-toggle" onClick={toggleMenu}>
            â˜°
          </Link>
        )}
      </div>
    </header>
  );
};

export default Header;
